t.script:
document.addEventListener('DOMContentLoaded', function() {
    const qrResult = document.getElementById('qr-result');
    const qrReader = new Html5Qrcode("qr-reader");
    const observacionesInput = document.getElementById('observaciones');

    function registrarVisita(qr, lat, lng, observaciones) {
        // Redirige al endpoint con los par치metros QR, ubicaci칩n y observaciones
        const params = new URLSearchParams({
            ubicacion_gps: lat + ',' + lng,
            observaciones: observaciones
        });
        window.location.href = `/registrar_visita/${qr}?${params.toString()}`;
    }

    function onScanSuccess(decodedText) {
        qrReader.stop();
        qrResult.style.display = 'block';
        qrResult.textContent = "Procesando...";
        // Obtener ubicaci칩n GPS
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(function(position) {
                registrarVisita(
                    decodedText,
                    position.coords.latitude,
                    position.coords.longitude,
                    observacionesInput.value
                );
            }, function(error) {
                qrResult.textContent = "No se pudo obtener la ubicaci칩n GPS.";
            });
        } else {
            qrResult.textContent = "El navegador no soporta GPS.";
        }
    }

    qrReader.start(
        { facingMode: "environment" },
        { fps: 10, qrbox: 250 },
        onScanSuccess
    );
});
